import React from "react";
import { Link } from "react-router-dom";

import SignOutButton from "../SignOut/index";
import * as routes from "../../constants/routes";
import "./../../assets/css/mobile-navigation.css";

const NavigationAuth = props => {
    const burgerToggle = () => {
        let linksEl = document.querySelector(".narrowLinks");
        if (linksEl.style.display === "block") {
            linksEl.style.display = "none";
        } else {
            linksEl.style.display = "block";
        }
    };

    const nightMode = { background: "#00252C", color: "#F4D680" };

    const nightModeHeader = {
        background: "#00252C",
        color: "#9AD3CB",
        fontSize: "23px!important"
    };

    const mightModeToggle = { background: "#00252C", color: "#F4D680" };

    const daymode = { background: "#f8f9fa", color: "gray" };
    const dayModeLink = { color: "gray" };
    const nightModeLink = { background: "#00252C", color: "#F4D680" };

    const navigationBar = window.screen.width < 720 ? (
        <nav style={props.settings.mode === "night" ? nightModeHeader : daymode}>
            <ul className="navbar-nav">
                <input type="checkbox" class="openSidebarMenu" id="openSidebarMenu" /><h2 className="navbar-brand">Expense Manager</h2>
            </ul>

            <label for="openSidebarMenu" class="sidebarIconToggle">
                <div class="spinner diagonal part-1"></div>
                <div class="spinner horizontal"></div>
                <div class="spinner diagonal part-2"></div>
            </label>
            <div id="sidebarMenu">
                <ul class="sidebarMenuInner">
                    <li>Jelena Jovanovic <span>Web Developer</span></li>
                    <li><a href="https://vanila.io" target="_blank">Company</a></li>
                    <li><a href="https://instagram.com/plavookac" target="_blank">Instagram</a></li>
                    <li><a href="https://twitter.com/plavookac" target="_blank">Twitter</a></li>
                    <li><a href="https://www.youtube.com/channel/UCDfZM0IK6RBgud8HYGFXAJg" target="_blank">YouTube</a></li>
                    <li><a href="https://www.linkedin.com/in/plavookac/" target="_blank">Linkedin</a></li>
                </ul>
            </div>
        </nav>
    ) :
        <nav style={props.settings.mode === "night" ? nightModeHeader : daymode}>
            <div className="navWide">
                <ul className="navbar-nav">
                    <h2 className="navbar-brand">Expense Manager</h2>
                </ul>
                <div className="wideDiv">
                    <Link
                        className={`nav-link ${window.location.pathname === "/home" ? "active" : "inactive"}`}
                        to={routes.HOME}
                        style={props.settings.mode === "night" ? nightModeLink : dayModeLink}
                    >
                        Home
                    </Link>
                    <Link
                        className={`nav-link ${window.location.pathname === "/month-view" ? "active" : "inactive"}`}
                        to={routes.MONTH_VIEW}
                        style={props.settings.mode === "night" ? nightModeLink : dayModeLink}
                    >
                        Monthly
                    </Link>
                    <Link
                        className={`nav-link ${window.location.pathname === "/daily-view" ? "active" : "inactive"}`}
                        to={routes.DAILY_VIEW}
                        style={props.settings.mode === "night" ? nightModeLink : dayModeLink}
                    >
                        Daily
                    </Link>
                    <Link
                        className={`nav-link ${window.location.pathname === "/filter-view" ? "active" : "inactive"}`}
                        to={routes.FILTER_VIEW}
                        style={props.settings.mode === "night" ? nightModeLink : dayModeLink}
                    >
                        filter
                    </Link>
                    <Link
                        className={`nav-link ${window.location.pathname === "/statistics" ? "active" : "inactive"}`}
                        to={routes.STATISTICS_VIEW}
                        style={props.settings.mode === "night" ? nightModeLink : dayModeLink}
                    >
                        Stats
                    </Link>
                    <Link
                        className={`nav-link ${window.location.pathname === "/loan" ? "active" : "inactive"}`}
                        to={routes.LOAN_VIEW}
                        style={props.settings.mode === "night" ? nightModeLink : dayModeLink}
                    >
                        Loan
                    </Link>
                    <Link
                        className={`nav-link ${window.location.pathname === "/savings" ? "active" : "inactive"}`}
                        to={routes.SAVINGS_VIEW}
                        style={props.settings.mode === "night" ? nightModeLink : dayModeLink}
                    >
                        Savings
                    </Link>
                    <Link
                        className={`nav-link ${window.location.pathname === "/settings" ? "active" : "inactive"}`}
                        to={routes.SETTINGS_VIEW}
                        style={props.settings.mode === "night" ? nightModeLink : dayModeLink}
                    >
                        Settings
                    </Link>
                    <Link className="nav-link" to={routes.SIGN_IN}>
                        <SignOutButton />
                    </Link>
                </div>
            </div>
        </nav>

    return navigationBar
};

const NavigationNonAuth = () => {
    const burgerToggle = () => {
        let linksEl = document.querySelector(".narrowLinks");
        if (linksEl.style.display === "block") {
            linksEl.style.display = "none";
        } else {
            linksEl.style.display = "block";
        }
    };

    return (
        <nav>
            <div className="navWide">
                <ul className="navbar-nav">
                    <h2 className="navbar-brand">Expense Manager</h2>
                </ul>
                <div className="wideDiv">
                    {/* <Link
                        className={`nav-link ${window.location.pathname === "/signin" ? "active" : "inactive"}`}
                        to={routes.SIGN_IN}
                    >
                        Sign In
                    </Link> */}

                    <Link
                        className={`nav-link ${window.location.pathname === "/shop" ? "active" : "inactive"}`}
                        to={routes.SHOP}
                    >
                        Shop
                    </Link>

                    <Link
                        className={`nav-link ${window.location.pathname === "/" ? "active" : "inactive"}`}
                        to={routes.SIGN_IN}
                    >
                        Sign In
                    </Link>
                </div>
            </div>
            <div className="navNarrow">
                <i className="fa fa-bars fa-2x" onClick={burgerToggle} />
                <ul className="navbar-nav">
                    <h2 className="navbar-brand">Expense Manager</h2>
                </ul>
                <div className="narrowLinks">
                    {/* <Link
                        className={`nav-link ${window.location.pathname === "/signin" ? "active" : "inactive"}`}
                        to={routes.SIGN_IN}
                        onClick={burgerToggle}
                    >
                        Sign In
                    </Link> */}

                    <Link
                        className={`nav-link ${window.location.pathname === "/shop" ? "active" : "inactive"}`}
                        to={routes.SHOP}
                    >
                        Shop
                    </Link>

                    <Link
                        className={`nav-link ${window.location.pathname === "/" ? "active" : "inactive"}`}
                        to={routes.SIGN_IN}
                        onClick={burgerToggle}
                    >
                        Sign In
                    </Link>
                </div>
            </div>
        </nav>
    );
};

const Navigation = ({ authUser, settings }) => {
    return (
        <div>
            {authUser && authUser.emailVerified && settings ? (
                <NavigationAuth settings={settings} />
            ) : (
                    <NavigationNonAuth />
                )}
        </div>
    );
};

export default Navigation;
